(2210, 170, '_form', '<div class=\"container\">\n<h3 class=\"heading-2\">General Information</h3>\n<div class=\"row\">\n<div class=\"col-md-6\">\n<div class=\"form-group\">\n    <label for=\"firstname\">First Name</label>\n    [text* first-name id:firstname class:form-control]\n</div>\n</div>\n<div class=\"col-md-6\">\n\n<div class=\"form-group\">\n    <label for=\"lastname\">Last Name</label>\n    [text* last-name id:lastname class:form-control]\n</div>\n\n</div>\n</div>\n\n<div class=\"row\">\n<div class=\"col-md-6\">\n<div class=\"form-group\">\n    <label for=\"company\">Company</label>\n    [text* company id:company class:form-control]\n</div>\n</div>\n<div class=\"col-md-6\">\n<div class=\"row\">\n<div class=\"col-md-4\">\n<div class=\"form-group\">\n    <label for=\"areacode\">Area Code</label>\n    [number* area-code id:areacode class:form-control]\n</div>\n</div>\n<div class=\"col-md-8\">\n<div class=\"form-group\">\n    <label for=\"phone\">Phone</label>\n    [number* phone id:phone class:form-control]\n</div></div>\n</div>\n</div>\n</div>\n<div class=\"row\">\n<div class=\"col-md-6\">\n<div class=\"form-group\">\n    <label for=\"email\">Email</label>\n    [email* email id:email class:form-control]\n</div>\n</div>\n<div class=\"col-md-6 hidden-xs\"></div>\n</div>\n\n<h3 class=\"heading-2\">Shipping Details</h3>\n\n\n<div class=\"row\">\n<div class=\"col-md-6\">\n<div class=\"form-group\">\n    <label for=\"firstname\">Number of Packages/month?</label>\n[radio radio-7 id:radio-ship default:1 \"Less than 500\" \"Between 500 and 1000\" \"More than 1000\"]\n\n</div>\n</div>\n<div class=\"col-md-6\">\n\n<div class=\"form-group\">\n    <label for=\"spaceneeded\">Space needed in Pallet(4x4)?</label>\n    [text* spaceneeded id:spaceneeded class:form-control]\n</div>\n\n</div>\n</div>\n\n\n<div class=\"row\">\n<div class=\"col-md-6\">\n\n<div class=\"form-group\">\n    <label for=\"spaceneeded\">Where do you Ship?</label>\n    [checkbox* checkbox-133 id:ship exclusive \"Canada\" \"USA\" \"International\"]\n</div>\n</div>\n<div class=\"col-md-6\">\n\n<div class=\"form-group\">\n    <label for=\"spaceneeded\">Additional Information</label>\n    [textarea* textarea-856 id:additonal-msg class:form-control]\n</div>\n\n</div>\n</div>\n\n\n\n[submit class:btn class:btn-custom \"Send\"]\n</div>'),
